const items = $input.all();
return items.map((item) => {
  try {
    const parsed = typeof item.json.payload === 'string' ? JSON.parse(item.json.payload) : item.json.payload;
    return { json: { ...item.json, parsed, parse_ok: true } };
  } catch (error) {
    return { json: { ...item.json, parse_ok: false, parse_error: error instanceof Error ? error.message : 'Parse error' } };
  }
});

const seen = new Set();
const unique = [];
for (const item of $input.all()) {
  const email = String(item.json.email ?? '').trim().toLowerCase();
  if (!email || seen.has(email)) continue;
  seen.add(email);
  unique.push({ json: item.json });
}
return unique;

const chunkSize = 50;
const items = $input.all();
const output = [];
for (let i = 0; i < items.length; i += chunkSize) {
  output.push({ json: { batch_index: i / chunkSize, payload: items.slice(i, i + chunkSize).map((x) => x.json) } });
}
return output;

return $input.all().map((item) => {
  const parsed = item.json.date ? new Date(String(item.json.date)) : null;
  return { json: { ...item.json, date_iso: parsed && !Number.isNaN(parsed.getTime()) ? parsed.toISOString() : null } };
});

const required = ['email', 'name'];
return $input.all().map((item) => {
  const missing = required.filter((field) => !item.json[field]);
  return { json: { ...item.json, is_valid: missing.length === 0, missing_fields: missing } };
});

const items = $input.all();
if (!items.length) return [{ json: { csv: '' } }];
const rows = items.map((x) => x.json);
const headers = Object.keys(rows[0]);
const lines = [headers.join(',')];
for (const row of rows) {
  lines.push(headers.map((h) => JSON.stringify(row[h] ?? '')).join(','));
}
return [{ json: { csv: lines.join('\n') } }];

const item = $input.first();
if (!item) return [];
const entries = Object.entries(item.binary ?? {});
if (!entries.length) return [{ json: { ...item.json, file_text: null, file_error: 'No binary data' } }];
const [name, data] = entries[0];
const text = Buffer.from(data.data ?? '', 'base64').toString('utf8');
return [{ json: { ...item.json, binary_name: name, file_text: text } }];

return $input.all().map((item) => {
  const workflow = item.json.workflow ?? 'workflow';
  const error = item.json.error ?? 'Error sin detalle';
  const id = item.json.id ?? 'n/a';
  return { json: { ...item.json, slack_message: 'ALERT ' + workflow + ' | item ' + id + ' | ' + error } };
});

const groups = new Map();
for (const item of $input.all()) {
  const k = String(item.json.status ?? 'unknown');
  const current = groups.get(k) ?? { status: k, count: 0 };
  current.count += 1;
  groups.set(k, current);
}
return [...groups.values()].map((x) => ({ json: x }));

const allowlist = ['id', 'name', 'email', 'status'];
return $input.all().map((item) => {
  const safe = {};
  for (const field of allowlist) {
    if (field in item.json) safe[field] = item.json[field];
  }
  return { json: safe };
});

return $input.all().map((item) => {
  const retry = Number(item.json.retry_count ?? 0);
  return { json: { ...item.json, retry_count: retry + 1, retry_at: new Date().toISOString() } };
});

const map = { firstName: 'first_name', lastName: 'last_name', createdAt: 'created_at' };
return $input.all().map((item) => {
  const out = {};
  for (const [k, v] of Object.entries(item.json)) out[map[k] ?? k] = v;
  return { json: out };
});

return $input.all().map((item) => {
  const n = Number(item.json.amount);
  return { json: { ...item.json, amount_number: Number.isFinite(n) ? n : 0 } };
});

return $input.all().map((item) => {
  const query = new URLSearchParams(item.json.params ?? {}).toString();
  return { json: { ...item.json, query } };
});
